-module(test_util).
-author('Carlos Gonzalez <carlosj.gf@gmail.com>').

-compile([export_all]).
-include_lib("eunit/include/eunit.hrl").
-include("nkbase.hrl").


start() ->
	ok = filelib:ensure_dir("data/ring/"),
	nkbase_app:start(),
    riak_core:wait_for_service(nkbase).


stop() ->
	application:stop(nkbase),
	application:stop(riak_core).


get_name() ->
	Pos1 = crypto:rand_uniform(1,201),
	Name1 = lists:nth(Pos1, names()),
	Pos2 = crypto:rand_uniform(1,1001),
	Name2 = lists:nth(Pos2, surnames()),
	Pos3 = crypto:rand_uniform(1,1001),
	Name3 = lists:nth(Pos3, surnames()),
	{Name1, Name2, Name3}.


